public class Program29 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m=5,n=5,i,j;
		for(i=0;i<n;i++) {
			for(j=0;j<m;j++) {
				if(i==0||i==n-1||j==0||j==m-1) {
				System.out.print("*");
			}
				else {
					System.out.println(" ");
				}
				System.out.println(" ");
		}

	}
	}
}
